mapper.select:
SELECT *
FROM (
        SELECT no,title,q_content,q_author,q_date,status,a_author, a_date, is_deleted, ROWNUM as rnum
        FROM (
                SELECT *
                FROM "BOARD_QNA"
                ORDER BY no desc
             )
     )
WHERE rnum BETWEEN #{start} and #{end}

SELECT *
FROM (
        SELECT no, title, q_content, q_author, q_date, a_author, status, is_deleted, ROWNUM as rnum
        FROM (
                SELECT *
                FROM "BOARD_QNA"

	                1=1
	                 AND title =#{title}
	                AND q_author =#{q_author}
	                AND status =#{status}

                ORDER BY no desc
             )
     )
WHERE rnum BETWEEN #{start} and #{end}

SELECT COUNT(*)
FROM (
        SELECT no, title, q_content, q_author, q_date, status, is_deleted, ROWNUM as rnum
        FROM (
                SELECT *
                FROM "BOARD_QNA"

	                1=1
	                 AND title =#{title}
	                AND q_author =#{q_author}
	                AND status =#{status}

                ORDER BY no
             )
     )

mapper.update:
UPDATE board_qna
SET is_deleted = 1
WHERE no = #{no}

UPDATE board_qna
SET status =  1, a_author = #{a_author}, a_date = SYSDATE, a_content = #{a_content}
WHERE no = #{no}
